import React, { useState } from 'react';

const SearchForm = ({ onSearch }) => {
  const [city, setCity] = useState('');
  const [checkIn, setCheckIn] = useState('');
  const [checkOut, setCheckOut] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form submit edildi:", city, checkIn, checkOut);
    onSearch({ city, checkIn, checkOut });
  };

  return (
    <form onSubmit={handleSubmit} style={formStyle}>
      <select value={city} onChange={(e) => setCity(e.target.value)} required style={inputStyle}>
        <option value="">Şehir Seçin</option>
        <option value="İstanbul">İstanbul</option>
        <option value="Ankara">Ankara</option>
        <option value="İzmir">İzmir</option>
        <option value="Antalya">Antalya</option>
        {/* Dilersen buraya diğer şehirleri de ekleyebilirsin */}
      </select>

      <input
        type="date"
        value={checkIn}
        onChange={(e) => setCheckIn(e.target.value)}
        required
        style={inputStyle}
      />
      <input
        type="date"
        value={checkOut}
        onChange={(e) => setCheckOut(e.target.value)}
        required
        style={inputStyle}
      />

      <button type="submit" style={buttonStyle}>Ara</button>
    </form>
  );
};

// Basit inline stil tanımları
const formStyle = {
  display: 'flex',
  gap: '10px',
  alignItems: 'center',
  padding: '20px',
  justifyContent: 'center',
};

const inputStyle = {
  padding: '8px',
  fontSize: '14px',
};

const buttonStyle = {
  padding: '8px 16px',
  fontSize: '14px',
  backgroundColor: '#4CAF50',
  color: 'white',
  border: 'none',
  cursor: 'pointer',
};

export default SearchForm;
