import React, { useState, useEffect } from 'react';
import { Routes, Route, useNavigate, Link } from 'react-router-dom';
import './App.css';
import SearchForm from './components/SearchForm';
import HotelList from './components/HotelList';
import LoginForm from './components/LoginForm';
import RegisterForm from './components/RegisterForm';
import AddHotel from './components/AddHotel';
import HotelDetail from './components/HotelDetail'; // üëà Yeni eklenen bile≈üen

function App() {
  const [hotels, setHotels] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      fetchUserData(token);
    }
  }, []);

  const fetchUserData = async (token) => {
    try {
      const response = await fetch('http://localhost:8000/users/me', {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (response.ok) {
        const user = await response.json();
        setCurrentUser(user);
      } else {
        handleLogout();
      }
    } catch (error) {
      console.error('Kullanƒ±cƒ± bilgileri alƒ±namadƒ±:', error);
      handleLogout();
    }
  };

  const handleSearch = async ({ city }) => {
    try {
      const response = await fetch(`http://localhost:8000/hotels?city=${encodeURIComponent(city)}`);
      if (!response.ok) throw new Error('Otel bilgileri alƒ±namadƒ±');
      const data = await response.json();
      setHotels(data);
    } catch (error) {
      console.error('Hata:', error);
      setHotels([]);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setCurrentUser(null);
    navigate('/');
  };

  const handleLogin = () => navigate('/login');
  const handleRegister = () => navigate('/register');

  const handleLoginSuccess = (user) => {
    setCurrentUser(user);
    navigate('/');
  };

  return (
    <div className="App">
      <header className="app-header">
        <Link to="/" style={{ textDecoration: 'none', color: 'inherit' }}>
          <h1>Otel Rezervasyon Sistemi üè®</h1>
        </Link>
        <div className="auth-buttons">
          {currentUser ? (
            <div className="user-actions">
              <span className="welcome-message">
                Ho≈ü geldiniz, {currentUser.full_name || currentUser.email}
              </span>
              {currentUser.is_admin && (
                <button className="admin-btn" onClick={() => navigate('/admin')}>
                  Admin Paneli
                </button>
              )}
              <button className="logout-btn" onClick={handleLogout}>√áƒ±kƒ±≈ü Yap</button>
            </div>
          ) : (
            <>
              <button className="login-btn" onClick={handleLogin}>Giri≈ü Yap</button>
              <button className="register-btn" onClick={handleRegister}>Kayƒ±t Ol</button>
            </>
          )}
        </div>
      </header>

      <main className="main-content">
        <Routes>
          <Route path="/" element={<><SearchForm onSearch={handleSearch} /><HotelList hotels={hotels} /></>} />
          <Route path="/login" element={<LoginForm onLoginSuccess={handleLoginSuccess} />} />
          <Route path="/register" element={<RegisterForm />} />
          <Route path="/admin" element={currentUser?.is_admin ? <AddHotel /> : <p>Yetkisiz eri≈üim</p>} />
          <Route path="/hotels/:id" element={<HotelDetail />} /> {/* üëà Yeni rota */}
        </Routes>
      </main>
    </div>
  );
}

export default App;
