import React, { useState } from 'react';

const SearchForm = ({ onSearch }) => {
  const [city, setCity] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form gönderildi:', city); // Kontrol için
    onSearch({ city });
  };

  return (
    <form onSubmit={handleSubmit} style={formStyle}>
      <select 
        value={city} 
        onChange={(e) => setCity(e.target.value)} 
        required 
        style={inputStyle}
      >
        <option value="">Şehir Seçin</option>
        <option value="Antalya">Antalya</option>
        <option value="İstanbul">İstanbul</option>
        <option value="İzmir">İzmir</option>
      </select>

      <button type="submit" style={buttonStyle}>Otel Ara</button>
    </form>
  );
};

const formStyle = {
  display: 'flex',
  justifyContent: 'center',
  gap: '10px',
  padding: '20px',
  backgroundColor: '#f5f5f5',
  borderRadius: '8px',
  margin: '20px auto',
  maxWidth: '600px'
};

const inputStyle = {
  padding: '10px',
  fontSize: '16px',
  borderRadius: '4px',
  border: '1px solid #ddd',
  minWidth: '200px'
};

const buttonStyle = {
  padding: '10px 20px',
  backgroundColor: '#4CAF50',
  color: 'white',
  border: 'none',
  borderRadius: '4px',
  cursor: 'pointer',
  fontSize: '16px'
};

export default SearchForm;
