import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const HotelDetail = () => {
  const { id } = useParams();
  const [hotel, setHotel] = useState(null);
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState("");
  const [rating, setRating] = useState(0);
  const [editReview, setEditReview] = useState(null);
  const [currentUser, setCurrentUser] = useState(null);

  const token = localStorage.getItem("token");

  // Kullanıcı bilgisi çek (opsiyonel, yorum sahibi kontrolü için)
  useEffect(() => {
    if (!token) return;
    fetch("http://localhost:8000/users/me", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setCurrentUser(data))
      .catch(() => setCurrentUser(null));
  }, [token]);

  // Otel bilgisi ve yorumları çek
  const fetchHotelAndReviews = () => {
    fetch(`http://localhost:8000/hotels/${id}`)
      .then((res) => res.json())
      .then((data) => setHotel(data))
      .catch((error) => console.error(error));

    fetch(`http://localhost:8000/hotels/${id}/reviews`)
      .then((res) => res.json())
      .then((data) => setComments(data))
      .catch((err) => console.error(err));
  };

  useEffect(() => {
    fetchHotelAndReviews();
  }, [id]);

  // Yorum ekleme
  const handleCommentSubmit = (e) => {
    e.preventDefault();
    if (!newComment.trim() || rating === 0) return;

    fetch(`http://localhost:8000/reviews`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({
        hotel_id: parseInt(id),
        comment: newComment,
        rating: rating,
      }),
    })
      .then((res) => res.json())
      .then(() => {
        setNewComment("");
        setRating(0);
        fetchHotelAndReviews();
      })
      .catch((err) => console.error(err));
  };

  // Yorum silme
  const handleDelete = async (reviewId) => {
    await fetch(`http://localhost:8000/reviews/${reviewId}`, {
      method: "DELETE",
      headers: { Authorization: `Bearer ${token}` },
    });
    fetchHotelAndReviews();
  };

  // Yorum güncelleme
  const handleUpdate = async () => {
    await fetch(`http://localhost:8000/reviews/${editReview.id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({
        hotel_id: parseInt(id),
        comment: editReview.comment,
        rating: editReview.rating,
      }),
    });
    setEditReview(null);
    fetchHotelAndReviews();
  };

  if (!hotel) return <p style={styles.loading}>Yükleniyor...</p>;

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>{hotel.name}</h2>
      <p><strong>Şehir:</strong> {hotel.city}</p>
      <p><strong>Açıklama:</strong> {hotel.description}</p>
      <p><strong>Fiyat:</strong> {hotel.price}₺</p>
      <p><strong>Ortalama Puan:</strong> {"⭐".repeat(Math.round(hotel.average_rating))} ({hotel.average_rating})</p>
      <p><strong>Deniz Kenarı:</strong> {hotel.is_near_sea ? "✅ Evet" : "❌ Hayır"}</p>
      <p><strong>Otopark:</strong> {hotel.has_parking ? "✅ Var" : "❌ Yok"}</p>

      <hr style={styles.hr} />

      {/* Yorumlar */}
      <h3>Yorumlar</h3>
      {comments.length > 0 ? (
        comments.map((c) => (
          <div key={c.id} style={styles.commentBox}>
            <span>{"⭐".repeat(c.rating)}</span>
            <p>{c.comment}</p>
            {currentUser && c.user_id === currentUser.id && (
              <>
                <button
                  onClick={() => setEditReview(c)}
                  style={styles.editButton}
                >
                  Düzenle
                </button>
                <button
                  onClick={() => handleDelete(c.id)}
                  style={styles.deleteButton}
                >
                  Sil
                </button>
              </>
            )}
          </div>
        ))
      ) : (
        <p>Henüz yorum yapılmamış.</p>
      )}

      {/* Yorum Ekleme Formu */}
      {!editReview && (
        <form onSubmit={handleCommentSubmit} style={styles.form}>
          <label>Puan:</label>
          <div>
            {[1, 2, 3, 4, 5].map((star) => (
              <span
                key={star}
                style={{
                  cursor: "pointer",
                  fontSize: "20px",
                  color: star <= rating ? "gold" : "gray",
                }}
                onClick={() => setRating(star)}
              >
                ★
              </span>
            ))}
          </div>

          <textarea
            placeholder="Yorumunuzu yazın..."
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
            style={styles.textarea}
          />

          <button type="submit" style={styles.button}>Gönder</button>
        </form>
      )}

      {/* Yorum Güncelleme Formu */}
      {editReview && (
        <div style={styles.form}>
          <label>Yorum Düzenle:</label>
          <textarea
            value={editReview.comment}
            onChange={(e) =>
              setEditReview({ ...editReview, comment: e.target.value })
            }
            style={styles.textarea}
          />
          <div>
            {[1, 2, 3, 4, 5].map((star) => (
              <span
                key={star}
                onClick={() => setEditReview({ ...editReview, rating: star })}
                style={{
                  cursor: "pointer",
                  fontSize: "20px",
                  color: star <= editReview.rating ? "gold" : "gray",
                }}
              >
                ★
              </span>
            ))}
          </div>
          <button onClick={handleUpdate} style={styles.button}>Güncelle</button>
          <button onClick={() => setEditReview(null)} style={styles.button}>İptal</button>
        </div>
      )}
    </div>
  );
};

const styles = {
  container: {
    maxWidth: "600px",
    margin: "40px auto",
    padding: "20px",
    border: "1px solid #ccc",
    borderRadius: "10px",
    backgroundColor: "#f9f9f9",
  },
  title: { marginBottom: "10px" },
  hr: { margin: "20px 0" },
  commentBox: { borderBottom: "1px solid #ddd", padding: "8px 0" },
  form: { marginTop: "15px", display: "flex", flexDirection: "column", gap: "10px" },
  textarea: { minHeight: "60px", padding: "8px", borderRadius: "5px", border: "1px solid #ccc" },
  button: {
    backgroundColor: "#4CAF50",
    color: "white",
    padding: "8px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    marginTop: "5px",
    marginRight: "5px",
  },
  loading: { textAlign: "center", marginTop: "50px" },
  editButton: {
    backgroundColor: "#2196F3",
    color: "white",
    padding: "4px 8px",
    marginRight: "5px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
  deleteButton: {
    backgroundColor: "#f44336",
    color: "white",
    padding: "4px 8px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default HotelDetail;
