import React from 'react';

const HotelList = ({ hotels }) => {
  if (hotels.length === 0) {
    return <p style={{ textAlign: 'center' }}>Hiç otel bulunamadı.</p>;
  }

  return (
    <div style={listStyle}>
      {hotels.map((hotel) => (
        <div key={hotel.id} style={cardStyle}>
          <h3>{hotel.name}</h3>
          <p>Şehir: {hotel.city}</p>
          <p>Fiyat: {hotel.price}₺</p>

        </div>
      ))}
    </div>
  );
};

const listStyle = {
  display: 'flex',
  flexWrap: 'wrap',
  gap: '20px',
  justifyContent: 'center',
  padding: '20px',
};

const cardStyle = {
  border: '1px solid #ccc',
  borderRadius: '10px',
  padding: '16px',
  width: '250px',
  boxShadow: '2px 2px 10px rgba(0,0,0,0.1)',
};

export default HotelList;
