import React, { useState, useEffect } from 'react';
import { useNavigate, Routes, Route, useLocation } from 'react-router-dom';
import './App.css';
import SearchForm from './components/SearchForm';
import HotelList from './components/HotelList';
import LoginForm from './components/LoginForm';
import RegisterForm from './components/RegisterForm';

function App() {
  const [hotels, setHotels] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) fetchUserData(token);
  }, []);

  const fetchUserData = async (token) => {
    try {
      const res = await fetch('http://localhost:8000/users/me', {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (res.ok) {
        const user = await res.json();
        setCurrentUser(user);
      }
    } catch (e) {
      console.error('Kullanƒ±cƒ± bilgisi alƒ±namadƒ±:', e);
    }
  };

  const handleSearch = async ({ city }) => {
    try {
      const res = await fetch(`http://localhost:8000/hotels?city=${encodeURIComponent(city)}`);
      if (!res.ok) throw new Error('Otel bilgileri alƒ±namadƒ±');
      const data = await res.json();
      setHotels(data);
    } catch (e) {
      console.error('Hata:', e);
      setHotels([]);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setCurrentUser(null);
    navigate('/');
  };

  return (
    <div className="App">
      {/* HER SAYFADA G√ñR√úNEN √úST MEN√ú */}
      <header className="app-header">
        <h1>Otel Arama Sistemi üè®</h1>
        <div className="auth-buttons">
          {currentUser ? (
            <>
              <span>Ho≈ü geldin, {currentUser.full_name || currentUser.email}</span>
              {currentUser.is_admin && <button onClick={() => navigate('/admin')}>Admin Paneli</button>}
              <button onClick={handleLogout}>√áƒ±kƒ±≈ü Yap</button>
            </>
          ) : (
            <>
              <button onClick={() => navigate('/login')}>Giri≈ü Yap</button>
              <button onClick={() => navigate('/register')}>Kayƒ±t Ol</button>
            </>
          )}
        </div>
      </header>

      <main className="main-content">
        {/* SADECE ANA SAYFADA ARAMA FORMU VE OTELLER G√ñR√úN√úR */}
        {location.pathname === '/' && (
          <>
            <SearchForm onSearch={handleSearch} />
            <HotelList hotels={hotels} />
          </>
        )}

        {/* Y√ñNLENDƒ∞Rƒ∞LEN SAYFALAR */}
        <Routes>
          <Route path="/login" element={<LoginForm />} />
          <Route path="/register" element={<RegisterForm />} />
        </Routes>
      </main>
    </div>
  );
}

export default App;
