import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const AdminPanel = ({ currentUser }) => {
  const [hotels, setHotels] = useState([]);
  const [users, setUsers] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    if (!currentUser?.is_admin) {
      navigate('/');
    }
    fetchHotels();
    fetchUsers();
  }, [currentUser]);

  const fetchHotels = async () => {
    const response = await fetch('http://localhost:8000/hotels');
    const data = await response.json();
    setHotels(data);
  };

  const fetchUsers = async () => {
    const response = await fetch('http://localhost:8000/users', {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('token')}`
      }
    });
    const data = await response.json();
    setUsers(data);
  };

  const deleteHotel = async (id) => {
    await fetch(`http://localhost:8000/hotels/${id}`, {
      method: 'DELETE',
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('token')}`
      }
    });
    fetchHotels();
  };

  const toggleAdmin = async (userId, isAdmin) => {
    await fetch(`http://localhost:8000/users/${userId}/toggle-admin`, {
      method: 'PUT',
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('token')}`,
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ is_admin: !isAdmin })
    });
    fetchUsers();
  };

  return (
    <div className="admin-panel">
      <h2>Admin Paneli</h2>
      
      <div className="admin-section">
        <h3>Otel Yönetimi</h3>
        <button onClick={() => navigate('/add-hotel')}>Yeni Otel Ekle</button>
        <div className="hotel-list">
          {hotels.map(hotel => (
            <div key={hotel.id} className="hotel-card">
              <h4>{hotel.name}</h4>
              <p>{hotel.city} - {hotel.price}₺</p>
              <div className="actions">
                <button onClick={() => navigate(`/edit-hotel/${hotel.id}`)}>Düzenle</button>
                <button onClick={() => deleteHotel(hotel.id)}>Sil</button>
              </div>
            </div>
          ))}
        </div>
      </div>

      <div className="admin-section">
        <h3>Kullanıcı Yönetimi</h3>
        <div className="user-list">
          {users.map(user => (
            <div key={user.id} className="user-card">
              <h4>{user.full_name}</h4>
              <p>{user.email}</p>
              <button 
                onClick={() => toggleAdmin(user.id, user.is_admin)}
                className={user.is_admin ? 'admin-btn' : 'user-btn'}
              >
                {user.is_admin ? 'Admin Yetkisini Kaldır' : 'Admin Yap'}
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
