// src/components/HotelDetail.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './HotelDetail.css';

function HotelDetail() {
  const { id } = useParams();
  const [hotel, setHotel] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [newReview, setNewReview] = useState("");
  const [rating, setRating] = useState(0);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`http://localhost:8000/hotels/${id}`)
      .then((res) => res.json())
      .then((data) => {
        setHotel(data);
        setLoading(false);
      });

    fetch(`http://localhost:8000/hotels/${id}/reviews`)
      .then((res) => res.json())
      .then((data) => setReviews(data));
  }, [id]);

  const handleReviewSubmit = (e) => {
    e.preventDefault();

    fetch(`http://localhost:8000/hotels/${id}/reviews`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        comment: newReview,
        rating: rating,
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        setReviews([...reviews, data]);
        setNewReview("");
        setRating(0);
      });
  };

  if (loading) return <p>Yükleniyor...</p>;
  if (!hotel) return <p>Otel bulunamadı.</p>;

  return (
    <div className="hotel-detail">
      <h1>{hotel.name}</h1>
      <p><strong>Şehir:</strong> {hotel.city}</p>
      <p>{hotel.description}</p>
      <p><strong>Fiyat:</strong> {hotel.price} ₺</p>
      <p><strong>Denize Yakın:</strong> {hotel.is_near_sea ? "Evet" : "Hayır"}</p>
      <p><strong>Otopark:</strong> {hotel.has_parking ? "Var" : "Yok"}</p>

      <hr />

      <h2>Yorumlar</h2>
      {reviews.length > 0 ? (
        <ul>
          {reviews.map((rev, index) => (
            <li key={index}>
              <strong>{rev.rating} ⭐</strong> - {rev.comment}
            </li>
          ))}
        </ul>
      ) : (
        <p>Henüz yorum yok.</p>
      )}

      <form onSubmit={handleReviewSubmit} className="review-form">
        <h3>Yorum Ekle</h3>
        <textarea
          value={newReview}
          onChange={(e) => setNewReview(e.target.value)}
          placeholder="Yorumunuzu yazın..."
          required
        />
        <select value={rating} onChange={(e) => setRating(Number(e.target.value))} required>
          <option value="">Puan seçin</option>
          {[1, 2, 3, 4, 5].map((num) => (
            <option key={num} value={num}>{num} ⭐</option>
          ))}
        </select>
        <button type="submit">Gönder</button>
      </form>
    </div>
  );
}

export default HotelDetail;
