import React, { useState } from 'react';

const SearchForm = ({ onSearch }) => {
  const [city, setCity] = useState('');
  const [checkIn, setCheckIn] = useState('');
  const [checkOut, setCheckOut] = useState('');

  // Türkiye'nin tüm şehirleri (alfabetik sıralı)
  const turkishCities = [
    "Adana", "Adıyaman", "Afyonkarahisar", "Ağrı", "Aksaray", "Amasya", "Ankara",
    "Antalya", "Ardahan", "Artvin", "Aydın", "Balıkesir", "Bartın", "Batman",
    "Bayburt", "Bilecik", "Bingöl", "Bitlis", "Bolu", "Burdur", "Bursa", "Çanakkale",
    "Çankırı", "Çorum", "Denizli", "Diyarbakır", "Düzce", "Edirne", "Elazığ",
    "Erzincan", "Erzurum", "Eskişehir", "Gaziantep", "Giresun", "Gümüşhane", "Hakkari",
    "Hatay", "Iğdır", "Isparta", "İstanbul", "İzmir", "Kahramanmaraş", "Karabük",
    "Karaman", "Kars", "Kastamonu", "Kayseri", "Kilis", "Kırıkkale", "Kırklareli",
    "Kırşehir", "Kocaeli", "Konya", "Kütahya", "Malatya", "Manisa", "Mardin",
    "Mersin", "Muğla", "Muş", "Nevşehir", "Niğde", "Ordu", "Osmaniye", "Rize",
    "Sakarya", "Samsun", "Şanlıurfa", "Siirt", "Sinop", "Sivas", "Şırnak",
    "Tekirdağ", "Tokat", "Trabzon", "Tunceli", "Uşak", "Van", "Yalova", "Yozgat",
    "Zonguldak"
  ];

  const handleSubmit = (e) => {
    e.preventDefault();
    // Check-out tarihi check-in'den önce olamaz
    if (checkIn && checkOut && new Date(checkOut) < new Date(checkIn)) {
      alert("Çıkış tarihi giriş tarihinden önce olamaz!");
      return;
    }
    onSearch({ city, checkIn, checkOut });
  };

  return (
    <form onSubmit={handleSubmit} style={formStyle}>
      <select
        value={city}
        onChange={(e) => setCity(e.target.value)}
        required
        style={selectStyle}
      >
        <option value="">Şehir Seçiniz</option>
        {turkishCities.map((cityName) => (
          <option key={cityName} value={cityName}>
            {cityName}
          </option>
        ))}
      </select>

      <input
        type="date"
        value={checkIn}
        onChange={(e) => setCheckIn(e.target.value)}
        required
        style={inputStyle}
        min={new Date().toISOString().split('T')[0]}
        placeholder="Giriş Tarihi"
      />

      <input
        type="date"
        value={checkOut}
        onChange={(e) => setCheckOut(e.target.value)}
        required
        style={inputStyle}
        min={checkIn || new Date().toISOString().split('T')[0]}
        placeholder="Çıkış Tarihi"
      />

      <button type="submit" style={buttonStyle}>
        Otel Ara
      </button>
    </form>
  );
};

// Güncellenmiş Stiller
const formStyle = {
  display: 'flex',
  gap: '15px',
  alignItems: 'center',
  padding: '20px',
  justifyContent: 'center',
  flexWrap: 'wrap',
  maxWidth: '900px',
  margin: '0 auto',
};

const selectStyle = {
  padding: '10px',
  fontSize: '16px',
  borderRadius: '5px',
  border: '1px solid #ddd',
  minWidth: '200px',
  flex: '1',
};

const inputStyle = {
  padding: '10px',
  fontSize: '16px',
  borderRadius: '5px',
  border: '1px solid #ddd',
  minWidth: '180px',
  flex: '1',
};

const buttonStyle = {
  padding: '10px 20px',
  fontSize: '16px',
  backgroundColor: '#4CAF50',
  color: 'white',
  border: 'none',
  borderRadius: '5px',
  cursor: 'pointer',
  flex: '1',
  maxWidth: '200px',
};

export default SearchForm;
