import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './App.css';
import SearchForm from './components/SearchForm';
import HotelList from './components/HotelList';

function App() {
  const [hotels, setHotels] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);
  const navigate = useNavigate();

  // Kullanƒ±cƒ± oturumunu kontrol et
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      fetchUserData(token);
    }
  }, []);

  const fetchUserData = async (token) => {
    try {
      const response = await fetch('http://localhost:8000/users/me', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      if (response.ok) {
        const user = await response.json();
        setCurrentUser(user);
      }
    } catch (error) {
      console.error('Kullanƒ±cƒ± bilgileri alƒ±namadƒ±:', error);
    }
  };

  const handleSearch = async ({ city }) => {
    try {
      const response = await fetch(`http://localhost:8000/hotels?city=${encodeURIComponent(city)}`);
      if (!response.ok) throw new Error('Otel bilgileri alƒ±namadƒ±');
      const data = await response.json();
      setHotels(data);
    } catch (error) {
      console.error('Hata:', error);
      setHotels([]);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setCurrentUser(null);
    navigate('/');
  };

  const handleLogin = () => {
    navigate('/login');
  };

  const handleRegister = () => {
    navigate('/register');
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>Otel Arama Sistemi üè®</h1>
        <div className="auth-buttons">
          {currentUser ? (
            <div className="user-actions">
              <span className="welcome-message">Ho≈ü geldiniz, {currentUser.full_name || currentUser.email}</span>
              {currentUser.is_admin && (
                <button 
                  className="admin-btn"
                  onClick={() => navigate('/admin')}
                >
                  Admin Paneli
                </button>
              )}
              <button 
                className="logout-btn"
                onClick={handleLogout}
              >
                √áƒ±kƒ±≈ü Yap
              </button>
            </div>
          ) : (
            <div className="auth-buttons">
              <button 
                className="login-btn"
                onClick={handleLogin}
              >
                Giri≈ü Yap
              </button>
              <button 
                className="register-btn"
                onClick={handleRegister}
              >
                Kayƒ±t Ol
              </button>
            </div>
          )}
        </div>
      </header>

      <main className="main-content">
        <SearchForm onSearch={handleSearch} />
        <HotelList hotels={hotels} />
      </main>
    </div>
  );
}

export default App;
