import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import './App.css';
import SearchForm from './components/SearchForm';
import HotelList from './components/HotelList';
import AdminPanel from './components/AdminPanel';
import LoginForm from './components/LoginForm';
import RegisterForm from './components/RegisterForm';

function App() {
  const [hotels, setHotels] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Sayfa y√ºklendiƒüinde kullanƒ±cƒ± oturumunu kontrol et
    const token = localStorage.getItem('token');
    if (token) {
      fetchUserData(token);
    }
  }, []);

  const fetchUserData = async (token) => {
    try {
      const response = await fetch('http://localhost:8000/users/me', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      if (response.ok) {
        const user = await response.json();
        setCurrentUser(user);
      }
    } catch (error) {
      console.error('Kullanƒ±cƒ± bilgileri alƒ±namadƒ±:', error);
    }
  };

  const handleSearch = async ({ city, checkIn, checkOut }) => {
    try {
      const params = new URLSearchParams({ city });
      if (checkIn) params.append('check_in', checkIn);
      if (checkOut) params.append('check_out', checkOut);

      const response = await fetch(`http://localhost:8000/hotels?${params}`);

      if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);

      const data = await response.json();
      setHotels(data);
    } catch (error) {
      console.error('Hata:', error);
      setHotels([]);
    }
  };

  const handleLogin = async (email, password) => {
    try {
      const response = await fetch('http://localhost:8000/token', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: `username=${encodeURIComponent(email)}&password=${encodeURIComponent(password)}`
      });

      if (!response.ok) throw new Error('Giri≈ü ba≈üarƒ±sƒ±z');

      const { access_token } = await response.json();
      localStorage.setItem('token', access_token);
      await fetchUserData(access_token);
      navigate('/');
    } catch (error) {
      console.error('Giri≈ü hatasƒ±:', error);
      alert('Giri≈ü ba≈üarƒ±sƒ±z!');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setCurrentUser(null);
    navigate('/');
  };

  return (
    <div className="App">
      <header>
        <h1>Otel Rezervasyon Sistemi üè®</h1>
        {currentUser ? (
          <div className="user-actions">
            <span>Ho≈ü geldiniz, {currentUser.full_name}</span>
            {currentUser.is_admin && (
              <button onClick={() => navigate('/admin')}>Admin Paneli</button>
            )}
            <button onClick={handleLogout}>√áƒ±kƒ±≈ü Yap</button>
          </div>
        ) : (
          <div className="auth-actions">
            <button onClick={() => navigate('/login')}>Giri≈ü Yap</button>
            <button onClick={() => navigate('/register')}>Kayƒ±t Ol</button>
          </div>
        )}
      </header>

      <Routes>
        <Route path="/" element={
          <>
            <SearchForm onSearch={handleSearch} />
            <HotelList hotels={hotels} />
          </>
        } />
        <Route path="/admin" element={
          <AdminPanel currentUser={currentUser} />
        } />
        <Route path="/login" element={
          <LoginForm onLogin={handleLogin} />
        } />
        <Route path="/register" element={
          <RegisterForm />
        } />
      </Routes>
    </div>
  );
}

// Router ile sarmalayƒ±cƒ±
export default function AppWrapper() {
  return (
    <Router>
      <App />
    </Router>
  );
}
