import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, useNavigate } from 'react-router-dom';
import './App.css';
import SearchForm from './components/SearchForm';
import HotelList from './components/HotelList';
import AdminPanel from './components/AdminPanel';
import LoginForm from './components/LoginForm';
import RegisterForm from './components/RegisterForm';

const AppContent = () => {
  const [hotels, setHotels] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);
  const navigate = useNavigate();

  // Kullanıcı oturumunu kontrol et
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      fetchUserData(token);
    }
  }, []);

  const fetchUserData = async (token) => {
    try {
      const response = await fetch('http://localhost:8000/users/me', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      if (response.ok) {
        const user = await response.json();
        setCurrentUser(user);
      }
    } catch (error) {
      console.error('Kullanıcı bilgileri alınamadı:', error);
    }
  };

  const handleSearch = async ({ city, checkIn, checkOut }) => {
    try {
      const params = new URLSearchParams({ city });
      const response = await fetch(`http://localhost:8000/hotels?${params}`);

      if (!response.ok) throw new Error('Otel bilgileri alınamadı');

      const data = await response.json();
      setHotels(data);
    } catch (error) {
      console.error('Hata:', error);
      setHotels([]);
    }
  };

  const handleLogin = async (email, password) => {
    try {
      const formData = new URLSearchParams();
      formData.append('username', email);
      formData.append('password', password);

      const response = await fetch('http://localhost:8000/token', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: formData
      });

      if (!response.ok) throw new Error('Giriş başarısız');

      const { access_token } = await response.json();
      localStorage.setItem('token', access_token);
      await fetchUserData(access_token);
      navigate('/');
    } catch (error) {
      console.error('Giriş hatası:', error);
      alert('Hatalı email veya şifre!');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setCurrentUser(null);
    navigate('/');
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>Otel Rezervasyon Sistemi 🏨</h1>
        <div className="auth-buttons">
          {currentUser ? (
            <>
              <span className="welcome-message">Hoş geldiniz, {currentUser.full_name}</span>
              {currentUser.is_admin && (
                <button className="admin-btn" onClick={() => navigate('/admin')}>
                  Admin Paneli
                </button>
              )}
              <button className="logout-btn" onClick={handleLogout}>
                Çıkış Yap
              </button>
            </>
          ) : (
            <>
              <button className="login-btn" onClick={() => navigate('/login')}>
                Giriş Yap
              </button>
              <button className="register-btn" onClick={() => navigate('/register')}>
                Kayıt Ol
              </button>
            </>
          )}
        </div>
      </header>

      <main className="app-content">
        <Routes>
          <Route path="/" element={
            <>
              <SearchForm onSearch={handleSearch} />
              <HotelList hotels={hotels} />
            </>
          } />
          <Route path="/admin" element={
            currentUser?.is_admin ? (
              <AdminPanel currentUser={currentUser} />
            ) : (
              <div className="unauthorized">
                <h2>Yetkisiz Erişim</h2>
                <p>Bu sayfayı görüntüleme yetkiniz yok.</p>
              </div>
            )
          } />
          <Route path="/login" element={
            <LoginForm onLogin={handleLogin} />
          } />
          <Route path="/register" element={
            <RegisterForm />
          } />
        </Routes>
      </main>
    </div>
  );
};

const App = () => {
  return (
    <BrowserRouter>
      <AppContent />
    </BrowserRouter>
  );
};
export default App;
