import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import ReviewForm from './ReviewForm';
import ReviewList from './ReviewList';


const HotelDetail = () => {
  const { id } = useParams();
  const [hotel, setHotel] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:8000/hotels/${id}`)
      .then((res) => {
        if (!res.ok) {
          throw new Error('Otel bulunamadı');
        }
        return res.json();
      })
      .then((data) => setHotel(data))
      .catch((error) => {
        console.error(error);
      });
  }, [id]);

  if (!hotel) {
    return <p>Yükleniyor...</p>;
  }

  return (
    <div style={containerStyle}>
      <h2>{hotel.name}</h2>
      <p><strong>Şehir:</strong> {hotel.city}</p>
      <p><strong>Açıklama:</strong> {hotel.description}</p>
      <p><strong>Fiyat:</strong> {hotel.price}₺</p>
      <p><strong>Deniz Kenarı:</strong> {hotel.is_near_sea ? '✅ Evet' : '❌ Hayır'}</p>
      <p><strong>Otopark:</strong> {hotel.has_parking ? '✅ Var' : '❌ Yok'}</p>
    </div>
  );
};

const containerStyle = {
  maxWidth: '600px',
  margin: '40px auto',
  padding: '20px',
  border: '1px solid #ccc',
  borderRadius: '10px',
  backgroundColor: '#f9f9f9',
};

export default HotelDetail;
