import React, { useEffect, useState } from 'react';

function ReviewList({ hotelId }) {
  const [reviews, setReviews] = useState([]);

  const fetchReviews = async () => {
    const res = await fetch(`http://localhost:8000/hotels/${hotelId}/reviews`);
    const data = await res.json();
    setReviews(data);
  };

  useEffect(() => {
    fetchReviews();
  }, [hotelId]);

  return (
    <div>
      <h3>Reviews:</h3>
      {reviews.length === 0 ? (
        <p>No reviews yet.</p>
      ) : (
        reviews.map((r) => (
          <div key={r.id}>
            <strong>{r.rating} / 5</strong>
            <p>{r.comment}</p>
          </div>
        ))
      )}
    </div>
  );
}

export default ReviewList;
