    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }
    const data = await response.json();
    console.log('API yanƒ±tƒ±:', data); // Gelen veriyi loglayƒ±n
    setHotels(data);
  } catch (error) {
    console.error('Otel verileri alƒ±namadƒ±:', error);
    setHotels([]); // Hata durumunda hotels state'ini temizleyin
  }
};



