import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AddHotel = () => {
  const [formData, setFormData] = useState({
    name: '',
    city: '',
    description: '',
    price: '',
    is_near_sea: false,
    has_parking: false
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch('http://localhost:8000/hotels', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${localStorage.getItem('token')}`
      },
      body: JSON.stringify({ ...formData, price: parseFloat(formData.price) })
    });
    if (response.ok) {
      navigate('/');
    } else {
      const errorData = await response.json();
      alert('Hata: ' + errorData.detail || 'Otel eklenemedi');
    }
  };

  return (
    <div className="add-hotel-form">
      <h2>Yeni Otel Ekle</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Otel Adı" value={formData.name} onChange={handleChange} required />
        <input type="text" name="city" placeholder="Şehir" value={formData.city} onChange={handleChange} required />
        <textarea name="description" placeholder="Açıklama" value={formData.description} onChange={handleChange} required />
        <input type="number" name="price" placeholder="Fiyat" value={formData.price} onChange={handleChange} required />
        <label>
          <input type="checkbox" name="is_near_sea" checked={formData.is_near_sea} onChange={handleChange} />
          Denize Yakın
        </label>
        <label>
          <input type="checkbox" name="has_parking" checked={formData.has_parking} onChange={handleChange} />
          Otopark Var
        </label>
        <button type="submit">Otel Ekle</button>
      </form>
    </div>
  );
};

export default AddHotel;
