import React, { useState } from 'react';

const SearchForm = ({ onSearch }) => {
  const [city, setCity] = useState('');
  const [checkIn, setCheckIn] = useState('');
  const [checkOut, setCheckOut] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Arama Parametreleri:', { city, checkIn, checkOut });
    onSearch({ city, checkIn, checkOut });
  };

  return (
    <form onSubmit={handleSubmit} style={formStyle}>
      <select
        value={city}
        onChange={(e) => setCity(e.target.value)}
        required
        style={inputStyle}
      >
        <option value="">Şehir Seçin</option>
        <option value="Antalya">Antalya</option>
        <option value="İstanbul">İstanbul</option>
        <option value="İzmir">İzmir</option>
      </select>

      <input
        type="date"
        value={checkIn}
        onChange={(e) => setCheckIn(e.target.value)}
        required
        style={inputStyle}
        min={new Date().toISOString().split('T')[0]} // Bugünden önceki tarihleri engelle
      />

      <input
        type="date"
        value={checkOut}
        onChange={(e) => setCheckOut(e.target.value)}
        required
        style={inputStyle}
        min={checkIn || new Date().toISOString().split('T')[0]} // Check-in'den önceki tarihleri engelle
      />

      <button type="submit" style={buttonStyle}>
        Otel Ara
      </button>
    </form>
  );
};

// Stiller (Aynı)
const formStyle = {
  display: 'flex',
  gap: '10px',
  alignItems: 'center',
  padding: '20px',
  justifyContent: 'center',
};

const inputStyle = {
  padding: '8px',
  fontSize: '14px',
  borderRadius: '4px',
  border: '1px solid #ddd',
};

const buttonStyle = {
  padding: '8px 16px',
  fontSize: '14px',
  backgroundColor: '#4CAF50',
  color: 'white',
  border: 'none',
  cursor: 'pointer',
  borderRadius: '4px',
};

export default SearchForm;
