import React, { useState } from 'react';

function AddHotel() {
  const [formData, setFormData] = useState({
    name: '',
    city: '',
    description: '',
    price: '',
    is_near_sea: false,
    has_parking: false,
  });

  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const newValue = type === 'checkbox' ? checked : value;
    setFormData({ ...formData, [name]: newValue });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');

    try {
      const response = await fetch('http://localhost:8000/hotels', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          ...formData,
          price: parseFloat(formData.price),
        }),
      });

      if (response.ok) {
        setMessage('Otel başarıyla eklendi!');
        setFormData({
          name: '',
          city: '',
          description: '',
          price: '',
          is_near_sea: false,
          has_parking: false,
        });
      } else {
        const errorData = await response.json();
        setMessage(`Hata: ${errorData.detail}`);
      }
    } catch (error) {
      console.error('Otel eklenirken hata:', error);
      setMessage('Bir hata oluştu.');
    }
  };

  return (
    <div className="add-hotel-form">
      <h2>Otel Ekle (Admin)</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Otel Adı:
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Şehir:
          <input
            type="text"
            name="city"
            value={formData.city}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Açıklama:
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Fiyat:
          <input
            type="number"
            name="price"
            value={formData.price}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Denize Yakın mı?
          <input
            type="checkbox"
            name="is_near_sea"
            checked={formData.is_near_sea}
            onChange={handleChange}
          />
        </label>

        <label>
          Otopark Var mı?
          <input
            type="checkbox"
            name="has_parking"
            checked={formData.has_parking}
            onChange={handleChange}
          />
        </label>

        <button type="submit">Otel Ekle</button>
      </form>

      {message && <p className="form-message">{message}</p>}

      <style jsx>{`
        .add-hotel-form {
          max-width: 400px;
          margin: 0 auto;
          padding: 1rem;
          border: 1px solid #ccc;
          border-radius: 12px;
          background-color: #f9f9f9;
        }

        h2 {
          text-align: center;
          margin-bottom: 1rem;
        }

        form {
          display: flex;
          flex-direction: column;
        }

        label {
          margin-bottom: 1rem;
          display: flex;
          flex-direction: column;
          font-weight: 600;
        }

        input[type="text"],
        input[type="number"],
        textarea {
          padding: 8px;
          border: 1px solid #ccc;
          border-radius: 6px;
          font-size: 1rem;
          margin-top: 4px;
        }

        input[type="checkbox"] {
          margin-top: 8px;
        }

        button {
          padding: 10px;
          font-size: 1rem;
          background-color: #007bff;
          color: white;
          border: none;
          border-radius: 8px;
          cursor: pointer;
          margin-top: 12px;
        }

        button:hover {
          background-color: #0056b3;
        }

        .form-message {
          margin-top: 1rem;
          text-align: center;
          font-weight: bold;
          color: green;
        }
      `}</style>
    </div>
  );
}

export default AddHotel;
