import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './App.css';
import SearchForm from './components/SearchForm';
import HotelList from './components/HotelList';

function App() {
  const [hotels, setHotels] = useState([]);
  const navigate = useNavigate();

  const handleSearch = async ({ city, checkIn, checkOut }) => {
    try {
      console.log('Arama Parametreleri:', { city, checkIn, checkOut });
      const response = await fetch(
        `http://localhost:8000/hotels?city=${encodeURIComponent(city)}`
      );

      if (!response.ok) throw new Error('Otel bulunamadƒ±');

      const data = await response.json();
      setHotels(data);
    } catch (error) {
      console.error('Hata:', error);
      setHotels([]);
    }
  };

  return (
    <div className="App">
      <header className="header">
        <h1>Otel Arama Sistemi üè®</h1>
        <div className="auth-buttons">
          <button 
            className="login-btn"
            onClick={() => navigate('/login')}
          >
            Giri≈ü Yap
          </button>
          <button 
            className="register-btn"
            onClick={() => navigate('/register')}
          >
            Kayƒ±t Ol
          </button>
        </div>
      </header>

      <div className="search-container">
        <SearchForm onSearch={handleSearch} />
      </div>

      <HotelList hotels={hotels} />
    </div>
  );
}

export default App;
