import React, { useState, useEffect } from 'react';
import { useNavigate, Routes, Route } from 'react-router-dom';
import './App.css';
import SearchForm from './components/SearchForm';
import HotelList from './components/HotelList';
import LoginForm from './components/LoginForm';
import RegisterForm from './components/RegisterForm';

function App() {
  const [hotels, setHotels] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) fetchUserData(token);
  }, []);

  const fetchUserData = async (token) => {
    try {
      const res = await fetch('http://localhost:8000/users/me', {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (res.ok) {
        const user = await res.json();
        setCurrentUser(user);
      }
    } catch (e) {
      console.error('Kullanıcı bilgileri alınamadı:', e);
    }
  };

  const handleSearch = async ({ city }) => {
    try {
      const res = await fetch(`http://localhost:8000/hotels?city=${encodeURIComponent(city)}`);
      if (!res.ok) throw new Error('Otel bilgileri alınamadı');
      const data = await res.json();
      setHotels(data);
    } catch (e) {
      console.error('Hata:', e);
      setHotels([]);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setCurrentUser(null);
    navigate('/');
  };

  const handleLogin = () => {
    navigate('/login');
  };

  const handleRegister = () => {
    navigate('/register');
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>Otel Arama Sistemi 🏨</h1>
        <div className="auth-buttons">
          {currentUser ? (
            <div className="user-actions">
              <span>Hoş geldiniz, {currentUser.full_name || currentUser.email}</span>
              {currentUser.is_admin && (
                <button onClick={() => navigate('/admin')}>Admin Paneli</button>
              )}
              <button onClick={handleLogout}>Çıkış Yap</button>
            </div>
          ) : (
            <div className="auth-buttons">
              <button onClick={handleLogin}>Giriş Yap</button>
              <button onClick={handleRegister}>Kayıt Ol</button>
            </div>
          )}
        </div>
      </header>

      <main className="main-content">
        <Routes>
          <Route
            path="/"
            element={
              <>
                <SearchForm onSearch={handleSearch} />
                <HotelList hotels={hotels} />
              </>
            }
          />
          <Route path="/login" element={<LoginForm />} />
          <Route path="/register" element={<RegisterForm />} />
          {/* İstersen admin vs sayfaları buraya ekle */}
        </Routes>
      </main>
    </div>
  );
}

export default App;
