import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const HotelDetail = () => {
  const { id } = useParams();
  const [hotel, setHotel] = useState(null);
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState("");
  const [rating, setRating] = useState(0);

  // Otel bilgisi ve yorumları çek
  useEffect(() => {
    // Otel bilgisi
    fetch(`http://localhost:8000/hotels/${id}`)
      .then((res) => {
        if (!res.ok) throw new Error("Otel bulunamadı");
        return res.json();
      })
      .then((data) => setHotel(data))
      .catch((error) => console.error(error));

    // Yorumları çek
    fetch(`http://localhost:8000/hotels/${id}/reviews`)
      .then((res) => {
        if (!res.ok) throw new Error("Yorumlar alınamadı");
        return res.json();
      })
      .then((data) => setComments(data))
      .catch((err) => console.error(err));
  }, [id]);

  // Yorum ekleme
  const handleCommentSubmit = (e) => {
    e.preventDefault();
    if (!newComment.trim() || rating === 0) return;

    fetch(`http://localhost:8000/reviews`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${localStorage.getItem("token")}`, // giriş yapılmalı
      },
      body: JSON.stringify({
        hotel_id: parseInt(id),
        text: newComment,
        rating: rating,
      }),
    })
      .then((res) => {
        if (!res.ok) throw new Error("Yorum eklenemedi");
        return res.json();
      })
      .then((data) => {
        setComments([...comments, data]); // yeni yorumu ekle
        setNewComment("");
        setRating(0);
      })
      .catch((err) => console.error(err));
  };

  if (!hotel) return <p style={styles.loading}>Yükleniyor...</p>;

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>{hotel.name}</h2>
      <p><strong>Şehir:</strong> {hotel.city}</p>
      <p><strong>Açıklama:</strong> {hotel.description}</p>
      <p><strong>Fiyat:</strong> {hotel.price}₺</p>
      <p><strong>Deniz Kenarı:</strong> {hotel.is_near_sea ? "✅ Evet" : "❌ Hayır"}</p>
      <p><strong>Otopark:</strong> {hotel.has_parking ? "✅ Var" : "❌ Yok"}</p>

      <hr style={styles.hr} />

      {/* Yorumlar */}
      <h3>Yorumlar</h3>
      {comments.length > 0 ? (
        comments.map((c) => (
          <div key={c.id} style={styles.commentBox}>
            <span>{"⭐".repeat(c.rating)}</span>
            <p>{c.text}</p>
          </div>
        ))
      ) : (
        <p>Henüz yorum yapılmamış.</p>
      )}

      {/* Yorum Ekleme Formu */}
      <form onSubmit={handleCommentSubmit} style={styles.form}>
        <label>Puan:</label>
        <div>
          {[1, 2, 3, 4, 5].map((star) => (
            <span
              key={star}
              style={{
                cursor: "pointer",
                fontSize: "20px",
                color: star <= rating ? "gold" : "gray",
              }}
              onClick={() => setRating(star)}
            >
              ★
            </span>
          ))}
        </div>

        <textarea
          placeholder="Yorumunuzu yazın..."
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
          style={styles.textarea}
        />

        <button type="submit" style={styles.button}>Gönder</button>
      </form>
    </div>
  );
};

const styles = {
  container: {
    maxWidth: "600px",
    margin: "40px auto",
    padding: "20px",
    border: "1px solid #ccc",
    borderRadius: "10px",
    backgroundColor: "#f9f9f9",
  },
  title: {
    marginBottom: "10px",
  },
  hr: {
    margin: "20px 0",
  },
  commentBox: {
    borderBottom: "1px solid #ddd",
    padding: "8px 0",
  },
  form: {
    marginTop: "15px",
    display: "flex",
    flexDirection: "column",
    gap: "10px",
  },
  textarea: {
    minHeight: "60px",
    padding: "8px",
    borderRadius: "5px",
    border: "1px solid #ccc",
  },
  button: {
    backgroundColor: "#4CAF50",
    color: "white",
    padding: "8px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
  loading: {
    textAlign: "center",
    marginTop: "50px",
  },
};

export default HotelDetail;
