import React from 'react';
import { Link } from 'react-router-dom'; // Link ekleyin

const HotelList = ({ hotels }) => {
  console.log('HotelList received:', hotels); // Props kontrolü
  
  // Dikkat: "hotels" yerine "hotels" yazılmalı (typo düzeltildi)
  if (!hotels || hotels.length === 0) {
    return <p style={{ textAlign: 'center', marginTop: '20px' }}>Hiç otel bulunamadı.</p>;
  }

  return (
    <div style={listStyle}>
      {hotels.map((hotel) => (
        <Link 
          to={`/hotels/${hotel.id}`} // Dinamik URL
          key={hotel.id} 
          style={{ textDecoration: 'none', color: 'inherit' }} // Link stilini sıfırla
        >
          <div style={cardStyle}>
            <h3>{hotel.name}</h3>
            <p><strong>Şehir:</strong> {hotel.city}</p>
            <p><strong>Fiyat:</strong> {hotel.price}₺</p>
            <p><strong>Açıklama:</strong> {hotel.description}</p>
            <p><strong>Deniz Kenarı:</strong> {hotel.is_near_sea ? '✅ Evet' : '❌ Hayır'}</p>
            <p><strong>Otopark:</strong> {hotel.has_parking ? '✅ Var' : '❌ Yok'}</p>
          </div>
        </Link>
      ))}
    </div>
  );
};

const listStyle = {
  display: 'grid',
  gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
  gap: '20px',
  padding: '20px',
  maxWidth: '1200px',
  margin: '0 auto'
};

const cardStyle = {
  border: '1px solid #e0e0e0',
  borderRadius: '8px',
  padding: '20px',
  boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
  backgroundColor: '#fff',
  cursor: 'pointer', // Kartın tıklanabilir olduğunu gösterir
  transition: 'transform 0.2s', // Hover efekti için
  '&:hover': {
    transform: 'scale(1.02)' // Kartı hafifçe büyüt
  }
};

export default HotelList;
